  gtk_tooltips_set_tip (tooltips, eventbox48, _("Ingres ODBC Driver\n\nThe Ingres ODBC driver requires a third-party ODBC driver manager.  The Ingres ODBC driver connects to local or remote databases to fetch or modify information.\n\nThe iiodbcadmn utility allows Linux, Unix or VMS users to configure ODBC data source information."), NULL);

  image340 = create_pixmap (IngresInstall, "info_16_2.ico");
  gtk_widget_show (image340);
  gtk_container_add (GTK_CONTAINER (eventbox48), image340);

  eventbox49 = gtk_event_box_new ();
